import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';

class Farmer extends Component {

    constructor(props) {
        super(props);
        this.state = {farmers: []};
    }

    componentDidMount() {
        fetch('/api/farmer')
            .then(response => response.json())
            .then(data => this.setState({farmers: data}));
    }

    render() {
        const {farmers} = this.state;

        const farmerList = farmers.map(farmer => {
            return <tr key={farmer.id}>
                <td>{farmer.name}</td>
                <td>{farmer.district}</td>
                <td>{farmer.contact}</td>
                <td>{farmer.landMapping}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/farmers/" + farmer.id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(farmer.id)}>Delete</Button>
                        <Button size="sm" color="dark" tag={Link} to={"/farmer/" + farmer.id}>Profile</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });

        return (
            <div>
            <h1>Farmers</h1>
                <Container>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="25%">Name</th>
                            <th width="25%">District</th>
                            <th width="25%">Contact</th>
                            <th width="25%">landMapping</th>
                        </tr>
                        </thead>
                        <tbody>
                        {farmerList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        )
            
    }

}

export default Farmer
