import React from "react";
import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route, useParams, Navigate} from "react-router-dom";
import { useState} from 'react';
// import { Alert } from 'react-alert'

const Login = () => {

    const { type } = useParams();

    const [userName, setUserName] = useState('');
    const [passward, setPassward] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const submit = (e) => {
        e.preventDefault();
        setSubmitted(true);
        console.log(userName);
        console.log(passward);
        sessionStorage.setItem("userName", userName);
        sessionStorage.setItem("passward", passward);
        sessionStorage.setItem("userType", type);
        
        //if((!sessionStorage.getItem("userName")) && (!sessionStorage.getItem("passward"))){
        // if(!sessionStorage.getItem("userName")){
        //     if(!sessionStorage.getItem("passward")){
        //         console.log("Enter correct userName and passward");
        //         alert("Enter correct userName and passward!")
        //     }
        // }
            // {sessionStorage.getItem("userName").length === 0 && sessionStorage.getItem("passward").length === 0
             
            // console.log("Enter correct userName and passward");
            //     alert("Enter correct userName and passward!")
            // }
        <Navigate replace to="/farmer" />
    }

    return (
        <div>
            <h1>Login : {type}</h1>
            <form action="">
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="UserName"
            name="userName"
            value={userName}
            onChange={e => setUserName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Passward"
            value={passward}
            onChange={e => setPassward(e.target.value)}
          />
        </div>
        <div className="form-group">
          <button className="btn btn-primary" onClick={submit}>
            Submit
          </button>
          <button className="btn btn-danger">
            Register
          </button>
        </div>
      </form>
        </div>
    )

}

export default Login
