import React from "react";
import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route, useParams, Navigate, useNavigate} from "react-router-dom";
import { useState, useEffect} from 'react';
// import { Alert } from 'react-alert'

<script src="https://apis.google.com/js/platform.js" async defer></script>

const Login = () => {

    const navigate = useNavigate();
    const { type } = useParams();

    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [passward, setPassward] = useState('');
    const [submitted, setSubmitted] = useState(false);
    //const [state, setState] = useState('')
    const [user, setUser] = useState([]);

    const submit = async (e) => {
        e.preventDefault();
        setSubmitted(true);
        //console.log(userName);
        //console.log(email);
        //console.log(passward);
        // sessionStorage.setItem("userName", userName);
        // sessionStorage.setItem("email", setEmail);
        // sessionStorage.setItem("passward", passward);
        // sessionStorage.setItem("userType", type);
        // useEffect(() => {
        //     fetch("/api/user/login").then(
        //         res => setState(res.data)
        //     )
        // })

      const userDetails = {
          userName : userName,
          email : email,
          passward : passward,
      }
 
      await fetch('/api/user/login', {
        method: 'POST',
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(userDetails),
      }).then(res => console.log(res.email)
            );
        console.log(user.userName);
        console.log(user);


        // fetch('/api/user/login')
        //   .then(res => res.json())
        //   .then(res => {
        //       console.log(res[0])
        //       this.setState({userDetails: res[0]}); 
        // })


        // not working
        // {<Navigate replace to="/farmer" />}
        navigate('/farmer');
    }

    return (
        <div>
            <h1>Login : {type}</h1>

            <div class="g-signin2" data-onsuccess="onSignIn"></div>
            <br></br>
            <form action="">
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="UserName"
            name="userName"
            value={userName}
            onChange={e => setUserName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Passward"
            value={passward}
            onChange={e => setPassward(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <button className="btn btn-primary" onClick={submit}>
            Submit
          </button>
          <button className="btn btn-danger">
            Register
          </button>
        </div>
      </form>
        </div>
    )

}

export default Login
