import React, { Component } from "react";
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';

class Contact extends Component {
  
  // Nested object
  //state = {
peopleDetails = {
    name: 'kapil',
    address: {
      colony: 'vaishnav nagar',
      city: 'Jaipur',
      state: 'Rajasthan'
    }
  };
  
  handleChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    // Creating a dummy object using spread operator
    var address = { ...this.peopleDetails.address }
  
    // Updating the city
    address.city = 'Kota';
   // address.name = value;
    this.setState({ address })
  }
  
  render() {
    return (
      <div style={{ margin: 200 }}>
        <h1>{this.peopleDetails.name}</h1>
        <h1>{this.peopleDetails.address.colony} {","}
          {this.peopleDetails.address.city}{", "}
          {this.peopleDetails.address.state}</h1>
        <button
          onClick={this.handleChange}
        >UpdateCity </button>

<Form >
                   
            <FormGroup>
                <Label for="name">Name</Label>
                <Input type="text" name="city" id="city"  value={this.state.address.city || ''}
                        onChange={this.handleChange} 
                        />
            </FormGroup>
            </Form>
      </div>
    );
  }
}
  
export default Contact;
