            body: data
            //body: JSON.stringify(activityDetails),
        });
    }
  };

  render() {
    return (
      <div>
        <input type="text" onChange={this.handleChange} />
        <br></br>
        <input type="file" onChange={this.handleFileChange} />
        <input type="button" onClick={this.upload} value="Upload" />
      </div>
    );
  }
}


export default FileUpload;
