import React from "react";
import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route, useParams, Navigate, useNavigate} from "react-router-dom";
import { useState, useEffect} from 'react';
// import { Alert } from 'react-alert'



const Login = () => {

    const navigate = useNavigate();
    const { type } = useParams();

    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [passward, setPassward] = useState('');
    const [submitted, setSubmitted] = useState(false);
    //const [state, setState] = useState('')
    //const [user, setUser] = useState([]);
    const [user,setUser] = useState({
      userName: "",
      email: "",
      passward: "",
      type: ""
    });

    const submit = async (e) => {
        e.preventDefault();
        setSubmitted(true);
        //console.log(userName);
        //console.log(email);
        //console.log(passward);
        
      const userDetails = {
          userName : userName,
          email : email,
          passward : passward,
      }
 
      await fetch('/api/user/login', {
        method: 'POST',
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(userDetails),
          })
          //.then(res => console.log(res[0]));
          .then(res => res.json())
            .then(res => {
                console.log(res[0]);
                //setUser(res[0]); 
                setUser(previousState => {
                  return { ...previousState, userName : userName,
                    email : email,
                    passward : passward, type:type }
                });
                //setUserName(res[0].userName)
            })

        console.log("user:"+user); 
        console.log("uname:"+ userName);
        console.log("uemail:"+email);
        //console.log(user[0]);
        
        sessionStorage.setItem("userName", userName);
        sessionStorage.setItem("email", email);
        sessionStorage.setItem("passward", passward);
        sessionStorage.setItem("userType", type);

   

        // not working
        // {<Navigate replace to="/farmer" />}
        navigate('/farmer');
    }





    return (
        <div className="form">
            <h1>Login : {type}</h1>

            
            <br></br>
            <form>
            <div className="form-group">
          <input
            type="text"
            className="input"
            placeholder="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            className="input"
            placeholder="Passward"
            value={passward}
            onChange={e => setPassward(e.target.value)}
          />
        </div>
       
        <div className="form-group">
          <button className="btn btn-primary" onClick={submit}>
            Submit
          </button>
          <button className="btn btn-danger">
            Register
          </button>
        </div>
      </form> 


        </div>
    )

}

export default Login
