import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import { withRouter } from '../components/WithRouter';

class Profile extends Component {

    constructor(props) {
        super(props);
        this.state = {activities: []};
    }

    componentDidMount() {
        let farmerId = this.props.params.id;
        console.log("farmer id : " + farmerId);
        fetch('/api/activity/'+farmerId)
            .then(response => response.json())
            .then(data => this.setState({activities: data}));
    }

    async remove(id) {
        console.log("delete id : " + id);
        await fetch(`/api/activity/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
             let updatedActivity = [...this.state.activities].filter(i => i._id !== id);
            // setTodos(todos.filter((todo) => todo.id !== id));
             this.setState({activities: updatedActivity});
        });

       // this.props.navigate('/activity');
    }

    render() {
        const {activities} = this.state;

        const activityList = activities.map(activity => {
            return <tr key={activity._id}>
                <td>{activity.name}</td>
                <td>{activity.startDate}</td>
                <td>{activity.endDate}</td>
                <td>{activity.activityProof}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/activity/" + activity._id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(activity._id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });

        let farmerId = this.props.params.id;
        console.log("activities._id : " + activities._id);
        const linkId = "/activity/add/" + farmerId;
        const peopleLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        const assetLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        const materialConsumedLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        return (
            <div>
            <h1>Activities : {activities._id}</h1>
                <Container>
                <div className="float-right">
                    <Button color="success" tag={Link} to={linkId}>Add Activity</Button>
                </div>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="25%">Name</th>
                            <th width="25%">Start Date</th>
                            <th width="25%">End Date</th>
                            <th width="25%">Activity Proof</th>
                        </tr>
                        </thead>
                        <tbody>
                        {activityList}
                        </tbody>

                        <thead>
                        <h3>People Involved</h3>
                        <tr>
                            <th width="33%">People Name</th>
                            <th width="33%">People Work</th>
                            <th width="33%">People Wages</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>

                    </Table>
                </Container>

                <h3>People Involved</h3>
                <Container>
                <div className="float-right">
                    <Button color="success" tag={Link} to={peopleLinkId}>Add People</Button>
                </div>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="33%">People Name</th>
                            <th width="33%">People Work</th>
                            <th width="33%">People Wages</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>
                    </Table>
                </Container>

                <h3>Asset Used</h3>
                <Container>
                <div className="float-right">
                    <Button color="success" tag={Link} to={assetLinkId}>Add People</Button>
                </div>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="25%">Asset Name</th>
                            <th width="25%">Asset Driver</th>
                            <th width="25%">Asset Details</th>
                            <th width="25%">Asset Wages</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>
                    </Table>
                </Container>

                <h3>Materials Consumed</h3>
                <Container>
                <div className="float-right">
                    <Button color="success" tag={Link} to={materialConsumedLinkId}>Add People</Button>
                </div>
                    <Table className="mt-4">
                        <thead>
                        <tr>
                            <th width="50%">Material Consumed Receipt</th>
                            <th width="50%">Material Consumed Details</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>
                    </Table>
                </Container>
            </div>

            
        )
            
    }

}

export default withRouter(Profile)
