import React, { Component } from 'react';
import { withRouter } from '../components/WithRouter';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import { Link } from 'react-router-dom';

class peopleEdit extends Component {

    peopleDetails = {
        _id : '',
        activityId : this.props.params.activityId,
        peopleName: '',
        peopleWork: '',
        peopleWages: '',
    };

    constructor(props) {
        super(props);
        this.state = {peopleDetails : this.peopleDetails};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    async componentDidMount() {
        let peopleId = this.props.params.peopleId;
        console.log("people id : " + peopleId);
        if(peopleId !== 'new'){
            fetch('/api/activity/1/'+ peopleId)
            .then(res => res.json())
            .then(res => {
                console.log(res[0])
                this.setState({peopleDetails: res[0]}); 
            })
        }
        
    };

    async handleSubmit(event) {
        event.preventDefault();
        const {peopleDetails} = this.state;
        console.log("peopleDetails : " + peopleDetails._id);
        await fetch('/api/activity/people/' + (peopleDetails._id ?  peopleDetails.activityId + "/" + peopleDetails._id : peopleDetails.activityId), {
            //await fetch('/api/activity/' + peopleDetails.activityId, {
            method: (peopleDetails._id) ? 'PUT' : 'POST',
            // method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(peopleDetails),
        });

        // this.props.navigate('/farmerProfile/' + peopleDetails.activityId);          
        this.props.navigate('/' + peopleDetails.activityId);
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let peopleDetails = {...this.state.peopleDetails};
        peopleDetails[name] = value;
        this.setState({peopleDetails});
    }

    //to handle input change.
    // handleChange(event) {
    //     const {name, value} = event.target
    //     this.setState({
    //         [name]: value
    //     }) 
    // }

    render() {
        const {peopleDetails} = this.state;
        return (
            <div>
                <h1>PeopleEdit</h1>
                <Container>
                <Form onSubmit={this.handleSubmit}>
                    {/* value={peopleDetails.peopleName || ''} value={peopleDetails.peopleWork || ''}  value={peopleDetails.peopleWages || ''}*/}
                    <FormGroup>
                        <Label for="name">Name</Label>
                        <Input type="text" name="name" id="name" 
                               onChange={this.handleChange} autoComplete="name"/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="work">Work</Label>
                        <Input type="text" name="work" id="work" 
                               onChange={this.handleChange} autoComplete="work"/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="wages">Wages</Label>
                        <Input type="number" name="wages" id="wages" 
                               onChange={this.handleChange} autoComplete="wages"/>
                    </FormGroup>
                    <FormGroup>
                        <Button color="primary" type="submit">Save</Button>{' '}
                        <Button color="secondary" tag={Link} to={"/farmerProfile/" + peopleDetails.activityId}>Cancel</Button>
                        {/* <Button color="secondary" onClick={() => navigate(-1)}>Cancel</Button> */}
                    </FormGroup>
                </Form>
                    
                </Container>
            </div>
        )
    }

}

export default withRouter(peopleEdit)
