import React, { Component } from "react";
import ReactDOM from "react-dom";
import axios from "axios";

class FileUpload extends Component {
   state={
    activityProof:'',
    dat:''
   }

   constructor(props) {
    super(props);
   }

    handleFileChange = e => {
   // this.setState({ file: e.target.files[0] });
   this.setState({activityProof: e.target.files[0] }); 
  };

  handleChange = e => {
   //this.setState({ text: e.target.value });
    this.setState({ dat: e.target.value }); 
  };

  upload = () => {
    if (this.state.file) {
      let formData = new FormData();
      formData.append("file", this.state.activityProof);
      formData.set("dat", this.state.dat);

      // formData.append('profileImage',{ name, uri, type})

// Example code
// data.append('file',{ 
//     name: 'img1',
//     uri: 'imgUri',
//     type: 'image/jpeg'
// })

    //   fetch
    //     .post('/api/activity/', data)
    //     .then(response => console.log(response))
    //     .catch(error => console.log(error));

        //  fetch('/api/activity/',{
        //      method: 'POST',
        //     headers: {
        //        // 'Accept': 'application/json',
        //         'Accept': "*/*",
        //         "Content-Type": "multipart/form-data",
        //     },
        //     body: data
          
        // });

        axios.post("/upload", formData, { // receive two parameter endpoint url ,form data 
           // headers: {'Content-Type': 'multipart/form-data' }
        //    headers: {
        //     'Accept': 'application/json',
        //     "Content-Type": "multipart/form-data",
        // },
        //body: formData
        })
      .then(res => { // then print response status
        console.log(res.statusText)
      })
    }
  };

  render() {
    return (
      <div>
        <input type="text"  onChange={this.handleChange} />
        <br></br>
        <input type="file"  onChange={this.handleFileChange} />
        <input type="button" onClick={this.upload} value="Upload" />
      </div>
    );
  }
}


export default FileUpload;
