// export default Contact 

import React, { Component } from "react";
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';

class Contact extends Component {
  
  // Nested object
  state = {
   // peopleDetails = {
    name: '',
    address: {
      colony: 'vaishnav nagar',
      city: '',
      state: 'Rajasthan'
    }
  };

  constructor(props) {
    super(props);
    this.state.address.city='';
    this.state.name='';
    this.handleChange = this.handleChange.bind(this);
  }
  
  handleChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    // Creating a dummy object using spread operator
    //var address = { ...this.state.address }
  
    // Updating the city
    //address.city = 'Kota';
    //address[name] = value;
    //this.setState({ address })

    var obj = { ...this.state }
    obj[name]=value;
    obj.address[name]=value
    this.setState({ obj })
  }
  
  render() {
    return (
      <div style={{ margin: 200 }}>
        <h1>{this.state.name}</h1>
        <h1>{this.state.address.colony} {","}
          {this.state.address.city}{", "}
          {this.state.address.state}</h1>
        <button
          onClick={this.handleChange}
        >UpdateCity </button>

      <Form >            
            <FormGroup>
                <Label for="city">city</Label>
                <Input type="text" name="city" id="city"  value={this.state.address.city || ''}
                        onChange={this.handleChange} 
                        />
            </FormGroup>
            <FormGroup>
                <Label for="name">name</Label>
                <Input type="text" name="name" id="name"  value={this.state.name || ''}
                        onChange={this.handleChange} 
                        />
            </FormGroup>
      </Form>
      </div>
    );
  }
}
  
export default Contact;
