import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';

class Farmer extends Component {

    constructor(props) {
        super(props);
        this.state = {farmers: []};
    }

    componentDidMount() {
        fetch('/api/farmers')
            .then(response => response.json())
            .then(data => this.setState({farmers: data}));
    }

    render() {

        const farmersList = farmers.map(farmer => {
            return <tr key={farmer.id}>
                <td style={{whiteSpace: 'nowrap'}}>{farmer.name}</td>
                <td>{farmer.district}</td>
                <td>{farmer.contact}</td>
                <td>{farmer.landMapping}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/farmers/" + farmer.id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(farmer.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });

        return (
            <h1>Farmers</h1>
        )
            
    }

}

export default Farmer
