import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import { withRouter } from '../components/WithRouter';

class Profile extends Component {
    
    // activityDetails = {
    //     _id : '',
    //     farmerId : this.props.params.id,
    //     name: '',
    //     startDate: '',
    //     endDate: '',
    //     activityProof: '',
    //      people : [],  
    //      asset : [], 
    //      material : []
    // };


    constructor(props) {
        super(props);
        this.state = {activities: []};
       // this.state = {activityDetails : this.activityDetails};
    }

    componentDidMount() {
        let farmerId = this.props.params.id;
        console.log("farmer id : " + farmerId);
        fetch('/api/activity/'+farmerId)
            .then(response => response.json())
            .then(data => {this.setState({activities: data})
           // this.setState({activityDetails: data})
           });
    }

    async remove(id) {
        console.log("delete id : " + id);
        await fetch(`/api/activity/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
             let updatedActivity = [...this.state.activities].filter(i => i._id !== id);
            // setTodos(todos.filter((todo) => todo.id !== id));
             this.setState({activities: updatedActivity});
        });

       // this.props.navigate('/activity');
    }

    render() {
        const {activities} = this.state;
       // const {activityDetails} = this.state;

       const activityList = activities.map(activity => {
         return   <><tr key={activity._id}>
                <td>{activity.name}</td>
                <td>{new Date(activity.startDate).getDate()}</td>
                <td>{new Date(activity.endDate).getDate()}</td>
                <td>{activity.activityProof}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/activity/" + activity._id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(activity._id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
            <tr><td colspan="4"><h4 style={{ color: "green" }}>People involved</h4></td>
            <td><Button color="success" tag={Link} to="">Add People</Button></td>
            </tr>
            <tr><td colspan="4" align="center"><Table striped hover bordered>
               <thead><tr> <th >People Name</th>
                   <th>People Work</th>
                   <th>People Wages</th><th></th>
                   </tr></thead></Table></td>
                    
            </tr>
            <tr><td>p1</td><td>w1</td><td>wa1</td><td></td>
            <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" >Edit</Button>
                        <Button size="sm" color="danger" >Delete</Button>
                    </ButtonGroup>
                </td> 
            </tr>
            </>
               
        });

       

        let farmerId = this.props.params.id;
        console.log("activities._id : " + activities._id);
        const linkId = "/activity/add/" + farmerId;
        // const peopleLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        // const assetLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        // const materialConsumedLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        return (
            <div>
           
                <Container>
                {/* <div className="float-right">
                    <Button color="success" tag={Link} to={linkId}>Add Activity</Button>
                </div> */}
                    <Table striped hover bordered>
                        <thead>
                        <tr><td><h4 style={{ color: "green" }}>Activities</h4></td><td></td><td></td><td></td></tr>
                        <tr>
                            <th>Name</th>
                            <th>Start Date</th>
                            <th>End Date</th>
                            <th>Activity Proof</th>  
                            <th><Button color="success" tag={Link} to="">Add Activity</Button></th>     
                        </tr>
                        </thead>
                       
                        <tbody>
                        {activityList}
                        </tbody>
                    </Table>

                        {/* <div style={{whiteSpace: 'nowrap'}}>
                            People Involved 
                            <Button style={{margin:10}} color="success" tag={Link} to={peopleLinkId}>Add People</Button>
                        </div>

                    <Table striped hover bordered>
                        <thead>
                        <tr>
                            
                            <th>People Name</th>
                            <th>People Work</th>
                            <th>People Wages</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>
                    </Table> */}
                        
{/* 
                        <div>
                            Asset Used
                            <Button style={{margin:10}}  color="success" tag={Link} to={assetLinkId}>Add Asset</Button>
                        </div>
                    <Table striped hover bordered>
                        <thead>
                        <tr>
                            <th>Asset Driver</th>
                            <th>Asset Details</th>
                            <th>Asset Wages</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>
                    </Table> */}

                        {/* <div>
                            Materials Consumed
                            <Button style={{margin:10}}  color="success" tag={Link} to={materialConsumedLinkId}>Add Materials</Button>
                        </div>
                    <Table striped hover bordered>
                        <thead>
                        <tr>
                            <th>Material Consumed Receipt</th>
                            <th>Material Consumed Details</th>
                        </tr>
                        </thead>
                        <tbody>
                        
                        </tbody>

                    </Table> */}
                </Container>

            </div>

            
        )
            
    }

}

export default withRouter(Profile)
