import React, { Component } from 'react';
import { withRouter } from '../components/WithRouter';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import { Link } from 'react-router-dom';

class peopleEdit extends Component {
   
    // peopleDetails = {
    //     _id : '',  //this is activity id
    //     activityId : this.props.params.activityId,
    //     farmerId : '',
    //     people : []
    //     // peopleName: '',
    //     // peopleWork: '',
    //     // peopleWages: '',
    // };
    // 
    peopleDetails = {
    _id: '',
    activityId : this.props.params.activityId,
    farmerId:'',
    people: {
        peopleId:'',
        peopleName: '',
        peopleWork: '',
        peopleWages: ''
    }
  };
    constructor(props) {
        super(props);
        this.state = {peopleDetails : this.peopleDetails};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange1 = this.handleChange1.bind(this);
      
    };

    async componentDidMount() {
        let peopleId = this.props.params.peopleId;
        const {peopleDetails} = this.state; 
        console.log("people id : " + peopleId);
        console.log("activityId in people : " + peopleDetails.activityId)
        if(peopleId !== 'new'){
            fetch('/api/activity/people/'+ peopleDetails.activityId + "/" + peopleId)
            .then(res => res.json())
            .then(res => {
                 console.log("res : "+ res[0].people.peopleName)
                // console.log("wsj:"+ res[0].farmerId + " / " + res[0]._id);
                // this.setState = ({peopleDetails.farmerId: res[0].farmerId}); 
                // this.setState({...this.state.peopleDetails, farmerId: res[0].farmerId});
                this.setState({peopleDetails: res[0]}); 
                console.log("tytey" + peopleDetails.farmerId + " / " + peopleDetails._id + " / " + peopleDetails.people.peopleName);
            })
        }
        
    };

    async handleSubmit(event) {
        event.preventDefault();
        const {peopleDetails} = this.state;
        console.log("peopleDetails : farmerID :  " + peopleDetails.farmerId);
        console.log("peopleDetails : activityId :  " + peopleDetails._id);
        await fetch('/api/activity/people/' + (peopleDetails.people.peopleId ?  peopleDetails._id + "/" + peopleDetails.people.peopleId : peopleDetails._id), {
            //await fetch('/api/activity/' + peopleDetails.activityId, {
            method: (peopleDetails.people.peopleId) ? 'PUT' : 'POST',
            // method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(peopleDetails),
        });

         this.props.navigate('/farmerProfile/' + peopleDetails._id);          
       // this.props.navigate('/' + peopleDetails._id);
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let peopleDetails = {...this.state.peopleDetails};
        peopleDetails[name] = value;
        this.setState({peopleDetails});
       
    }
    handleChange1 = input => e => {
        const { target } = e;
       // var people = { ...this.peopleDetails.people }
  
        // Updating the city
       // people.peopleName = 'Kota';
       // this.setState({ people })
        this.setState({ people: { ...this.state.people, peopleName: "kota" } })
    }

    // handleChange(val) {
    //     return val;
    // }
    // //to handle input change.
    // handleChange(event) {
    //     const {name, value} = event.target
    //     this.setState({
    //         [name]: value
    //     }) 
    // }

    render() {
        const {peopleDetails} = this.state;
        
        return (
            <div>
                <h1>PeopleEdit</h1>
                <Container>
                <Form onSubmit={this.handleSubmit}>
                    {/* value={peopleDetails.peopleName || ''} value={peopleDetails.peopleWork || ''}  value={peopleDetails.peopleWages || ''}*/}
                    <FormGroup>
                        <Label for="name">Name</Label>
                        <input type="text" name="name" id="name"  value={peopleDetails.people.peopleName || ''}
                               onChange={this.handleChange1} 
                               />
                    </FormGroup>
                    {/* <FormGroup>
                        <Label for="work">Work</Label>
                        <Input type="text" name="work" id="work"  defaultValue={peopleDetails.people.peopleWork}
                                //onChange={()=>{this._handleChangeEvent(peopleDetails.people.peopleWork);}}
                               //onChange={this.handleChange} 
                               autoComplete="work"/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="wages">Wages</Label>
                        <Input type="number" name="wages" id="wages"  defaultValue={peopleDetails.people.peopleWages}
                               // onChange={()=>{this._handleChangeEvent(peopleDetails.people.peopleWages);}}
                               //onChange={this.handleChange} 
                               autoComplete="wages"/>
                    </FormGroup> */}
                    <FormGroup>
                        <Button color="primary" type="submit">Save</Button>{' '}
                        <Button color="secondary" tag={Link} to={"/farmerProfile/" + peopleDetails.farmerId}>Cancel</Button>
                        {/* <Button color="secondary" onClick={() => navigate(-1)}>Cancel</Button> */}
                    </FormGroup>
                </Form>
                    
                </Container>
            </div>
        )
    }

}

export default withRouter(peopleEdit)
