import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
import { withRouter } from '../components/WithRouter';

class Profile extends Component {
    
    // activityDetails = {
    //     _id : '',
    //     farmerId : this.props.params.id,
    //     name: '',
    //     startDate: '',
    //     endDate: '',
    //     activityProof: '',
    //      people : [],  
    //      asset : [], 
    //      material : []
    // };


    constructor(props) {
        super(props);
        this.state = {activities: []};
       // this.state = {activityDetails : this.activityDetails};
    }

    componentDidMount() {
        let farmerId = this.props.params.id;
        console.log("farmer id : " + farmerId);
        fetch('/api/activity/'+farmerId)
            .then(response => response.json())
            .then(data => {this.setState({activities: data})
           // this.setState({activityDetails: data})
           });
    }

    async remove(id) {
        console.log("delete id : " + id);
        await fetch(`/api/activity/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
             let updatedActivity = [...this.state.activities].filter(i => i._id !== id);
            // setTodos(todos.filter((todo) => todo.id !== id));
             this.setState({activities: updatedActivity});
        });

       // this.props.navigate('/activity');
    }

    render() {
        const {activities} = this.state;
        const mystyle = {
            // color: "white",
            // backgroundColor: "DodgerBlue",
            // padding: "10px",
            // fontFamily: "Arial",
            position:"relative",
            left:"100px", 
            top:"2px", 
            // right: "80px"
          };
      
      
          let farmerId = this.props.params.id;
        
          const linkId = "/activity/" + farmerId + "/";
          const peopleLinkId = "/activity/people/" ;

       const activityList = activities.map((activity,index) => {
         return   <>
            <div ><br></br><br></br><h5>Activities:{index+1}</h5></div>
            <Table >
                <thead>
               
                <tr>
                    <th width="20%">Name</th>
                    <th width="20%" >Start Date</th>
                    <th width="20%" >End Date</th>
                    <th width="20%" >Activity Proof</th>  
                    <th width="20%" ><ButtonGroup><Button color="success" tag={Link} to={linkId + "new"}>Add Activity</Button> <Button color="success" onClick={() => this.remove(activity._id)}>Del Activity</Button></ButtonGroup></th>     
                </tr>
                </thead>

                <tr key={activity._id}>
                <td>{activity.name}</td>
                <td>{new Date(activity.startDate).getDate()}</td>
                <td>{new Date(activity.endDate).getDate()}</td>
                <td>{activity.activityProof}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={linkId + activity._id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(activity._id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        </Table>
        
        <div style = {mystyle}>
            {activity.map((people) => {
                <table width="50%" > <thead>     
                <tr> <th width="25%" >People Name</th>
                    <th width="25%">People Work</th>
                    <th width="25%">People Wages</th>
                    <th width="20%" align="right"><Button color="success" tag={Link} to={peopleLinkId + activity._id + "/new"}>Add People</Button></th>
                 </tr></thead>    
           
                 <tr>    
                     <td >people.peopleName</td><td>people.peopleWork</td><td>people.peopleWages</td>
                     <td><ButtonGroup><Button size="sm" color="primary" tag={Link} to={peopleLinkId + activity._id }>Edit</Button><Button size="sm" color="danger" >Delete</Button></ButtonGroup></td> 
                 </tr>
             </table>
            })
            }
            
        </div>

        <div style = {mystyle}>
            <table  width="50%"> <thead>     
               <tr> <th width="25%" >Asset Driver</th>
                   <th width="25%">Asset Details</th>
                   <th width="25%">Asset Wages</th>
                   <th width="25%" align="right"><Button color="success" tag={Link} to="">Add Asset</Button></th>
                </tr></thead>    
          
                 {/* <tr>    
                    <td >p1</td><td>w1</td><td>wa1</td>
                    <td><ButtonGroup><Button size="sm" color="primary" >Edit</Button><Button size="sm" color="danger" >Delete</Button></ButtonGroup></td> 
                </tr>
                <tr>    
                    <td >p1</td><td>w1</td><td>wa1</td>
                    <td><ButtonGroup> <Button size="sm" color="primary" >Edit</Button> <Button size="sm" color="danger" >Delete</Button> </ButtonGroup></td> 
                </tr>  */}
            </table>
        </div>

        <div style = {mystyle}>
            <table width="50%"> <thead>     
               <tr> <th width="30%" >Material Consumed Receipt</th>
                   <th width="30%">Material Consumed Details</th>
                   <th width="20%" align="right"><Button color="success" tag={Link} to="">Add Materials</Button></th>
                </tr></thead>    
          
                 {/* <tr>    
                    <td >p1</td><td>w1</td><td>wa1</td>
                    <td><ButtonGroup><Button size="sm" color="primary" >Edit</Button><Button size="sm" color="danger" >Delete</Button></ButtonGroup></td> 
                </tr>
                <tr>    
                    <td >p1</td><td>w1</td><td>wa1</td>
                    <td><ButtonGroup> <Button size="sm" color="primary" >Edit</Button> <Button size="sm" color="danger" >Delete</Button> </ButtonGroup></td> 
                </tr>  */}
            </table>
        </div>

            </>
               
        });

       

        // let farmerId = this.props.params.id;
        // console.log("activities._id : " + activities._id);
        // const linkId = "/activity/add/" + farmerId;
        // const peopleLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        // const assetLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        // const materialConsumedLinkId = "/activity/people/add/" + farmerId + "/" + activities._id;
        return (
            <div>    
                <Container> {activityList} </Container>
            </div>

            
        )
            
    }

}

export default withRouter(Profile)
